// Contract to possess staked tokens and to distribute rewards.

pragma solidity ^0.6.0;

import "./libs/SafeMath.sol";
import "./RewardToken.sol";

contract Stake is RewardToken {

    using SafeMath for uint256;

    address public owner;
    uint256 public stake_ids;
    uint256 public apy;
    mapping(address => uint256) public stakerId; // associate staker's id to their address.
    RewardToken public tokenContract;
    uint256 private totalStakedTokens;
    mapping(address => uint256) private airDrop; // the first ten accounts to interact with this contract automatically receives 1000 tokens.

    /**
    * Staker profile. An object to keep record of staked amount and period.
    */
    struct Staker {
        uint256 id;
        uint256 staked_amount; // the amount of token locked for staking.
        uint256 starting_date;
        uint256 total_balance; // the sum of staked tokens and rewards.
        uint256 stake_period;
        bool withdrawalable; // cannot withdraw if current time is less than starting_date + stake_period.
    }

    constructor(address _tokenAddress) public {
        tokenContract = RewardToken(_tokenAddress);
        owner = msg.sender;
    }

    function distributeTokens(address to) public returns(bool success) {
        require(msg.sender == owner, "Unauthorized caller.");
        require(airDrop[to] == 0, "Not eligible for airdrops.");
        require(tokenContract.balanceOf(owner) > 0, "Distributed tokens exceeded 10000, airdrop is over.");
        tokenContract.transfer(to, 1000);
        return true;
    }
}
